mapper.select:
SELECT COUNT(*) FROM calender 
WHERE prdID=#{prdID} 
AND userID=#{userID} 
AND prdType=#{prdType};

UPDATE calender 
SET prdName = #{prdName}  
WHERE dataID=#{dataID} 
AND userID=#{userID} ;

SELECT * FROM calender   
WHERE userID=#{userID} 
AND dataID=#{dataID};

mapper.update:
UPDATE calender 
SET prdName = #{prdName},
	calSubDate = #{calSubDate}, 
	calTransfer = #{calTransfer},  
	calMaturity = #{calMaturity},  
	calPayment = #{calPayment}
WHERE dataID = #{dataID}
AND	userID = #{userID};

mapper.select:
SELECT SUM(calPayment) FROM calender 
WHERE userID=#{userID} 
AND prdType=#{prdType};
